// uniqChildren() was generated by Bard

export function uniqChildren(regions) {
  const offices = regions.flatMap(region => region.offices);  // flatten the regions array into a single array of offices
  const uniqueOfficeObjects = new Map();  // create a map to store the unique office objects

  // iterate over the offices array and add each office object to the map
  offices.forEach(office => {
    uniqueOfficeObjects.set(office.code, office);
  });

  return [...uniqueOfficeObjects.values()]; // return an array of the unique office objects
}

